
'use strict';const form=document.querySelector('form');const fields=form.querySelectorAll('.form__input');const addErrorBorder=(el)=>{el.style.borderColor=el.validity.valid?'#e6e8f0':'red';};fields.forEach((field)=>{field.addEventListener('change',()=>{addErrorBorder(field);});});const checkFieldValidity=()=>{fields.forEach((field)=>{addErrorBorder(field);});};form.querySelector('.button[type=submit]').addEventListener('click',()=>{checkFieldValidity();});form.addEventListener('submit',()=>{checkFieldValidity();});